import { useEffect, useRef, useState } from 'react';
import './App.css';
import txt from './inputs/example';

const SQUARE_HEIGHT = 1;

function App() {
  const [positions, setPositions] = useState([]);
  const canvas = useRef(null);

  useEffect(() => {
    const lines = txt.split('\n');
    const startPositions = lines.map((line) => {
      const [, x, y] = line.match(/position=<([^,]+),([^>]+)/);
      const [, dx, dy] = line.match(/velocity=<([^,]+),([^>]+)/);
      return { x: +x, y: +y, dx: +dx, dy: +dy };
    });
    setPositions(startPositions);
  }, []);

  // useEffect(() => {
  if (canvas.current) {
    // show top left pixel
    const ctx = canvas.current.getContext('2d');
    const W = ctx.canvas.width,
      H = ctx.canvas.height;
    const xPositions = positions.map(({ x }) => x);
    const yPositions = positions.map(({ y }) => y);
    const leftBound = Math.min(...xPositions, 0);
    const rightBound = Math.max(...xPositions, 0);
    const topBound = Math.max(...yPositions, 0);

    const bottomBound = Math.max(...yPositions, 0);
    const maxDistance = Math.max(
      Math.abs(leftBound),
      Math.abs(rightBound),
      Math.abs(topBound),
      Math.abs(bottomBound)
    );
    console.log(leftBound, rightBound, topBound, bottomBound, maxDistance);
    // canvas height -> 400px -> max 200px either way
    // every time over 200 scale the pixels
    const distanceModulo = Math.floor(maxDistance / (W / 2));
    const scaleFactor = 1 / (distanceModulo + 1);
    console.log(distanceModulo, scaleFactor);

    ctx.setTransform(scaleFactor, 0, 0, scaleFactor, W / 2, H / 2); // moves the origin to the center of
    // the canvas
  }
  // }, [positions]);

  // draw squares on canvas
  positions.forEach((position) => {
    if (!canvas.current) return;
    const ctx = canvas.current.getContext('2d');
    ctx.beginPath();
    ctx.lineWidth = '1';
    ctx.strokeStyle = 'black';
    ctx.rect(
      position.x * SQUARE_HEIGHT,
      position.y * SQUARE_HEIGHT,
      SQUARE_HEIGHT,
      SQUARE_HEIGHT
    );
    ctx.stroke();
    ctx.fill();
  });

  return (
    <div className="App">
      <h1>Logic Blocks</h1>
      <button>Next cycle</button>
      <canvas
        ref={canvas}
        height="400px"
        width="400px"
        style={{ border: '1px solid black' }}
      ></canvas>
    </div>
  );
}

export default App;
